client/src/pages/migration-safety.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Loader2, AlertTriangle, CheckCircle2, Clock, Shield } from "lucide-react";
import { Separator } from "@/components/ui/separator";
import { ScrollArea } from "@/components/ui/scroll-area";

interface MigrationAnalysis {
  analysis: string;
  saferRewrite: string;
  warnings: string[];
  estimatedLockTime: string;
  recommendations: string[];
}

export default function MigrationSafety() {
  const [migration, setMigration] = useState("");
  const [databaseType, setDatabaseType] = useState("postgresql");
  const [result, setResult] = useState<MigrationAnalysis | null>(null);

  const analyzeMutation = useMutation({
    mutationFn: async (data: { migration: string; databaseType: string }) => {
      const response = await fetch("/api/analyze-migration", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to analyze migration");
      return response.json();
    },
    onSuccess: (data) => {
      setResult(data);
    },
  });

  const handleAnalyze = () => {
    if (!migration.trim()) return;
    analyzeMutation.mutate({ migration, databaseType });
  };

  return (
    <div className="min-h-screen bg-background p-4 md:p-6 lg:p-8">
      <div className="max-w-7xl mx-auto space-y-6">
        <div>
          <h1 className="text-3xl md:text-4xl font-bold flex items-center gap-2 mb-2">
            <Shield className="h-8 w-8 md:h-10 md:w-10 text-primary" />
            Migration Safety Assistant
          </h1>
          <p className="text-muted-foreground text-sm md:text-base">
            Analyze SQL migrations for potential risks and get safer alternatives
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card className="h-fit">
            <CardHeader>
              <CardTitle>Your Migration</CardTitle>
              <CardDescription>Paste your SQL migration or schema change</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="database-type">Database Type</Label>
                <Select value={databaseType} onValueChange={setDatabaseType}>
                  <SelectTrigger id="database-type" className="w-full">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="postgresql">PostgreSQL</SelectItem>
                    <SelectItem value="mysql">MySQL</SelectItem>
                    <SelectItem value="sqlserver">SQL Server</SelectItem>
                    <SelectItem value="oracle">Oracle</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="migration">SQL Migration</Label>
                <Textarea
                  id="migration"
                  placeholder="ALTER TABLE users ADD COLUMN email VARCHAR(255) NOT NULL;&#10;CREATE INDEX idx_users_email ON users(email);"
                  value={migration}
                  onChange={(e) => setMigration(e.target.value)}
                  className="font-mono text-sm min-h-[200px] md:min-h-[300px]"
                />
              </div>

              <Button
                onClick={handleAnalyze}
                disabled={!migration.trim() || analyzeMutation.isPending}
                className="w-full"
              >
                {analyzeMutation.isPending ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Analyzing...
                  </>
                ) : (
                  <>
                    <Shield className="mr-2 h-4 w-4" />
                    Analyze Migration
                  </>
                )}
              </Button>

              {analyzeMutation.isError && (
                <Alert variant="destructive">
                  <AlertTriangle className="h-4 w-4" />
                  <AlertTitle>Error</AlertTitle>
                  <AlertDescription>
                    {(analyzeMutation.error as Error).message}
                  </AlertDescription>
                </Alert>
              )}
            </CardContent>
          </Card>

          <div className="space-y-6">
            {result && (
              <>
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Clock className="h-5 w-5" />
                      Estimated Lock Time
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold text-primary">
                      {result.estimatedLockTime}
                    </div>
                  </CardContent>
                </Card>

                {result.warnings.length > 0 && (
                  <Card className="border-orange-500/50">
                    <CardHeader>
                      <CardTitle className="flex items-center gap-2 text-orange-600">
                        <AlertTriangle className="h-5 w-5" />
                        Warnings
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <ScrollArea className="h-[150px]">
                        <ul className="space-y-2">
                          {result.warnings.map((warning, idx) => (
                            <li key={idx} className="flex gap-2 text-sm">
                              <Badge variant="destructive" className="shrink-0">
                                {idx + 1}
                              </Badge>
                              <span>{warning}</span>
                            </li>
                          ))}
                        </ul>
                      </ScrollArea>
                    </CardContent>
                  </Card>
                )}

                <Card>
                  <CardHeader>
                    <CardTitle>Analysis</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <ScrollArea className="h-[200px]">
                      <p className="text-sm whitespace-pre-wrap">{result.analysis}</p>
                    </ScrollArea>
                  </CardContent>
                </Card>

                <Card className="border-green-500/50">
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2 text-green-600">
                      <CheckCircle2 className="h-5 w-5" />
                      Safer Rewrite
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <ScrollArea className="h-[200px]">
                      <pre className="text-sm font-mono bg-muted p-4 rounded-lg overflow-x-auto">
                        {result.saferRewrite}
                      </pre>
                    </ScrollArea>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <CheckCircle2 className="h-5 w-5" />
                      Recommendations
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <ScrollArea className="h-[250px]">
                      <ol className="space-y-3 list-decimal list-inside">
                        {result.recommendations.map((rec, idx) => (
                          <li key={idx} className="text-sm">
                            <span className="ml-2">{rec}</span>
                          </li>
                        ))}
                      </ol>
                    </ScrollArea>
                  </CardContent>
                </Card>
              </>
            )}

            {!result && !analyzeMutation.isPending && (
              <Card className="border-dashed">
                <CardContent className="flex flex-col items-center justify-center py-16 text-center">
                  <Shield className="h-16 w-16 text-muted-foreground mb-4" />
                  <p className="text-muted-foreground text-sm md:text-base px-4">
                    Enter your SQL migration and click "Analyze Migration" to get safety insights
                  </p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/new-schema.tsx.content:
import { useState, useMemo } from "react";
import { AIInput } from "@/components/ai-input";
import { SchemaOutput } from "@/components/schema-output";
import { ERDiagram } from "@/components/er-diagram";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Save, Code2, Network, Database } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { motion, AnimatePresence } from "framer-motion";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { schemaToNodesAndEdges } from "@/lib/schema-to-diagram";

const DATABASE_TYPES = [
  { value: "PostgreSQL", label: "PostgreSQL" },
  { value: "MySQL", label: "MySQL" },
  { value: "MongoDB", label: "MongoDB" },
  { value: "SQLite", label: "SQLite" },
  { value: "Oracle", label: "Oracle" },
  { value: "SQL Server", label: "SQL Server" },
];



export default function NewSchema() {
  const [isGenerating, setIsGenerating] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [generatedSchema, setGeneratedSchema] = useState<any>(null);
  const [activeView, setActiveView] = useState<"code" | "diagram">("code");
  const [databaseType, setDatabaseType] = useState("PostgreSQL");
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [prompt, setPrompt] = useState("");
  const { toast } = useToast();
  const [, navigate] = useLocation();
  const[Redirect,setRedirect]=useState("");

  const diagramData = useMemo(() => {
    if (!generatedSchema?.schemas?.sql) {
      return { nodes: [], edges: [] };
    }
    return schemaToNodesAndEdges(generatedSchema.schemas.sql);
  }, [generatedSchema]);

  const handleGenerate = async (userPrompt: string) => {
    setIsGenerating(true);
    setPrompt(userPrompt);

    try {
      const response = await fetch("/api/generate-schema", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: userPrompt,
          databaseType,
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to generate schema");
      }

      const result = await response.json();
      setGeneratedSchema(result);


      if (!title) {
        const words = userPrompt.split(" ").slice(0, 5).join(" ");
        setTitle(words.charAt(0).toUpperCase() + words.slice(1));
      }
      if (!description) {
        setDescription(userPrompt);
      }

      toast({
        title: "Schema generated!",
        description: "Your AI-powered database schema is ready",
      });
    } catch (error: any) {
      toast({
        title: "Generation failed",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsGenerating(false);
    }
  };

  const handleSave = async () => {
    if (!generatedSchema || !title || !description) {
      toast({
        title: "Missing information",
        description: "Please provide a title and description",
        variant: "destructive",
      });
      return;
    }

    setIsSaving(true);
    try {
      const response = await fetch("/api/projects", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title,
          description,
          prompt,
          databaseType,
          schemas: generatedSchema.schemas,
          explanation: generatedSchema.explanation,
          normalizationSuggestions: generatedSchema.normalizationSuggestions,
          queryExamples: generatedSchema.queryExamples,
          migrationScript: generatedSchema.migrationScript,
          dockerfile: generatedSchema.dockerfile,
          dockerCompose: generatedSchema.dockerCompose,
        }),
      });
      toast({
        title: "Saved feature coming soon!",
        description: "this feature is under development. wait for next update!",
      });

      if (!response.ok) {
        throw new Error("Failed to save project");
      }


      setIsSaving(false);




    } catch (error) {

    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="min-h-screen p-6 lg:p-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.4 }}
        className="max-w-7xl mx-auto space-y-8"
      >
        <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold tracking-tight" data-testid="text-page-title">AI Schema Generator</h1>
            <p className="text-muted-foreground mt-1">
              Describe your database in natural language and let AI do the rest
            </p>
          </div>
          {generatedSchema && (
            <Button onClick={handleSave} disabled={isSaving} className="gap-2" data-testid="button-save-project">
              <Save className="h-4 w-4" />
              {isSaving ? "Saving..." : "Save Project"}
            </Button>
          )}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="space-y-6">
            <div className="rounded-2xl border border-card-border bg-card/80 backdrop-blur-xl p-6 space-y-4">
              <div className="flex items-center gap-2">
                <Database className="h-5 w-5 text-primary" />
                <h3 className="text-lg font-semibold">Database Configuration</h3>
              </div>

              <div className="space-y-4">
                <div>
                  <Label htmlFor="database-type">Database Type</Label>
                  <Select value={databaseType} onValueChange={setDatabaseType}>
                    <SelectTrigger id="database-type" data-testid="select-database-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {DATABASE_TYPES.map((type) => (
                        <SelectItem key={type.value} value={type.value}>
                          {type.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {generatedSchema && (
                  <>
                    <div>
                      <Label htmlFor="project-title">Project Title</Label>
                      <Input
                        id="project-title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        placeholder="My Database Project"
                        data-testid="input-project-title"
                      />
                    </div>
                    <div>
                      <Label htmlFor="project-description">Description</Label>
                      <Input
                        id="project-description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        placeholder="A brief description of your project"
                        data-testid="input-project-description"
                      />
                    </div>
                  </>
                )}
              </div>
            </div>

            <AIInput onGenerate={handleGenerate} isGenerating={isGenerating} />
          </div>

          <div className="space-y-4">
            <AnimatePresence mode="wait">
              {generatedSchema ? (
                <motion.div
                  key="result"
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  transition={{ duration: 0.3 }}
                  className="space-y-4"
                >
                  <Tabs value={activeView} onValueChange={(v) => setActiveView(v as "code" | "diagram")}>
                    <TabsList className="grid w-full grid-cols-2">
                      <TabsTrigger value="code" className="gap-2" data-testid="tab-code-view">
                        <Code2 className="h-4 w-4" />
                        Code
                      </TabsTrigger>
                      <TabsTrigger value="diagram" className="gap-2" data-testid="tab-diagram-view">
                        <Network className="h-4 w-4" />
                        Diagram
                      </TabsTrigger>
                    </TabsList>
                    <TabsContent value="code" className="mt-6">
                      <SchemaOutput {...generatedSchema} />
                    </TabsContent>
                    <TabsContent value="diagram" className="mt-6">
                      <ERDiagram 
                        initialNodes={diagramData.nodes} 
                        initialEdges={diagramData.edges} 
                      />
                    </TabsContent>
                  </Tabs>
                </motion.div>
              ) : (
                <motion.div
                  key="placeholder"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                  className="flex items-center justify-center h-96 rounded-2xl border-2 border-dashed border-border bg-card/30 backdrop-blur-sm"
                >
                  <p className="text-muted-foreground">Your generated schema will appear here</p>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>
      </motion.div>
    </div>
  );
}

